import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import { busca } from '../api/api';
import '../assets/css/blog.css'
import { useHistory } from 'react-router'

const ListaCategorias = () => {
    const [categorias, setCategorias] = useState([])
    let history = useHistory()
    useEffect(() => {
        busca(`/categorias`, setCategorias).catch(   // Estou pegando o erro 404 e tratando-o, dando um push para a página de erro que criei ('/404')
            () => {
                history.push('/404')
            }
        )
    }, [])

    return (
        <ul className='lista-categorias container flex'>
            {
                categorias.map((categoria) => {
                    return (
                        <Link to={`/categoria/${categoria.id}`} key={categoria.id}>
                            <li className={`lista-categorias__categoria lista-categorias__categoria--${categoria.id}`}>
                                {categoria.nome}
                            </li>
                        </Link>
                    );
                })
            }
        </ul>
    );
}

export default ListaCategorias
